            ]
        };

        // Variables

        // end Variable
        document.title = "Application Library";

    }

    // /http://localhost:8000/www/getarticledetail/docker-zero-to-advance
    componentDidMount() {
        fetch("http://localhost:8000/www/getarticledetail/docker-zero-to-advance")
        .then(res => res.json())
        .then(
          (result) => {
            //me.setState({
             //   "latestArticles":result.data.latestArticles
           // });
           this.setState({
               "articles":result.data.latestArticles,
               "mobileApplications":result.data.mobileAppLists
           });
            this.props.parentmenucallback(result.data.category);
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            this.setState({
              isLoaded: true,
            });
          }
        ) 
    }
}

