import { Component } from 'react';


class HomePageController extends Component {
    constructor(props) {
        super(props);
        this.state ={
            isOpenSlider:true,
            activeBannerTab : 'forsale',
            latestArticles : [
                // {"id":1,title:"Docker Tutorial From Zero to Advance",image:"media1.png",category:[{id:1,name:"Docker"},{id:2,name:"DevOps"}],description:"hello world",releaseDate:"april, 21, 2022"},
                // {"id":2,title:"Docker Tutorial From Zero to Advance",image:"media2.png",category:[{id:1,name:"Docker"},{id:2,name:"DevOps"}],description:"hello world",releaseDate:"January, 21, 2022"},
                // {"id":3,title:"Docker Tutorial From Zero to Advance",image:"media3.png",category:[{id:1,name:"Docker"},{id:2,name:"DevOps"}],description:"hello world",releaseDate:"February, 22, 2022"},
                // {"id":4,title:"Docker Tutorial From Zero to Advance",image:"media2.png",category:[{id:1,name:"Docker"},{id:2,name:"DevOps"}],description:"hello world",releaseDate:"June, 02, 2022"},
                // {"id":5,title:"Docker Tutorial From Zero to Advance",image:"media3.png",category:[{id:1,name:"Server"},{id:2,name:"DevOps"}],description:"hello world",releaseDate:"August, 21, 2022"},
                // {"id":6,title:"Docker Tutorial From Zero to Advance",image:"media1.png",category:[{id:2,name:"Programming"}],description:"hello world",releaseDate:"March, 22, 2022"},
            ],
            mobileApplications : [
                {"id":1,title:"QR Code Scanner",image:"m1.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
                {"id":1,title:"Myanmar Calculator",image:"m2.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
                {"id":1,title:"Singapore Currency Exchange Rate",image:"m3.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
                {"id":1,title:"Myanmar Currency Exchange Rate",image:"m4.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
                {"id":1,title:"Testing xx",image:"m1.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
                {"id":1,title:"Testing zz",image:"m3.png",available:[{id:1,name:"GoogleStore",link:"https://www.google"},{id:1,name:"AppleStore",link:"https://www.google"}],description:"hello world"},
            ],
        };

        // Variables

        // end Variable
        document.title = "Application Library";



        this.onNavBarTroggleClick = this.onNavBarTroggleClick.bind(this);

        this.onForSaleTabClick = this.onForSaleTabClick.bind(this);
        this.onForRentTabClick = this.onForRentTabClick.bind(this);
        this.onProjectPlanTabClick = this.onProjectPlanTabClick.bind(this);

    }

    componentDidMount(){
      //  alert('ok');
      //this.props.parentmenucallback("hello");
      let me = this;
      fetch("http://localhost:8000/www/gethomedata/")
      .then(res => res.json())
      .then(
        (result) => {
          me.setState({
              "latestArticles":result.data.latestArticles
          });
          me.props.parentmenucallback(result.data.category);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    }
    onForSaleTabClick(){
        this.setState({
            activeBannerTab : 'forsale'
        });
    }
    onForRentTabClick(){
        this.setState({
            activeBannerTab : 'forrent'
        });
    }
    onProjectPlanTabClick(){
        this.setState({
            activeBannerTab : 'projectplan'
        });
    }

    onNavBarTroggleClick(){
        this.setState({
            isOpenSlider : !this.state.isOpenSlider
        });
    }
}

export {HomePageController};
