import React from 'react';
import './Article.css';
import {ArticlePageController} from './ArticlePageController';
import {
    DivMainContainer,
    DivMainContainerInner,
    DivMainBody,
    DivMainLeft,DivMainLeftInner,
    DivMainRight,
    Header,HeaderImage,HeaderContent,
    DivAuthorContainer,DivAuthorImage,DivAuthorInfo,ReleaseDate,

    Title,
    CategoryContainer,
    AButton,

    DivMainContainerBody

}
    from './ArticlePageElement';



import {RelatedItem} from '../../components/Items';

import Gist from "react-gist";

class ArticlePage extends ArticlePageController {

    render() {

        let article = this.state.detail;
        console.log(article);

        let datas = this.state.articles.map((element,i)=>{
            return (<RelatedItem item={element} key={"listiemsmall"+i} />);
        });


        // l


        return (
            <DivMainContainer>
                <DivMainContainerInner>
                    <Header>  
                        <HeaderImage>
                            <img src={article.image} width="100%" />
                        </HeaderImage> 
                        <HeaderContent>
                            <Title>{article.title}</Title>
                            <CategoryContainer>
                                <AButton>Dokcer</AButton>
                                <AButton marginLeft="20px">AWS</AButton>
                            </CategoryContainer>
                            <DivAuthorContainer>
                                <DivAuthorImage>
                                    <img src="m2.png" width="100%" />
                                </DivAuthorImage>
                                <DivAuthorInfo>
                                    <label>Author : Aung Kyaw Nyunt</label>
                                    <ReleaseDate>March, 22, 2022</ReleaseDate>
                                </DivAuthorInfo>
                            </DivAuthorContainer>
                        </HeaderContent>
                    </Header>


                </DivMainContainerInner>

                <DivMainContainerBody>
                    <DivMainBody>
                        <DivMainLeft>
                            <DivMainLeftInner 
                                style={{backgroundColor:"#3e4048"}} 
                                className="divContent"
                            >
                                <div dangerouslySetInnerHTML={{
                                    __html: article.intro
                                }} >
                                </div>
                            </DivMainLeftInner>
                            <DivMainLeftInner 

                                className="divContent"
                            >
                                <div dangerouslySetInnerHTML={{
                                    __html: article.description
                                }} >
                                </div>
                                <Gist id="50a6033c38dcceaf858eb95764926140" />
                            </DivMainLeftInner>
                        </DivMainLeft>
                        <DivMainRight>
                            {datas}<br/>
                        </DivMainRight>
                    </DivMainBody>
                </DivMainContainerBody>



            </DivMainContainer>
        );
    }
}



export {ArticlePage};
