import styled from "styled-components";


import latestArticle from '../../assets/images/bg1.png';

import {DefaultColor} from '../../components/config';

export const DivMainContainer = styled.div`
  display:flex;
  flex-direction:column;
  width: 100%;
  justify-content:center;
  align-items:center;
  background-image:url(${latestArticle});
  background-position:fixed;
  background-size: cover;
  background-attachment: fixed !important;
`;
export const DivMainContainerInner = styled.div`
  display:flex;
  flex-direction:row;
  background-color:green;
  width: 80%;

`;

export const DivMainLeft = styled.div`
  width:60%;
`;
export const DivMainRight = styled.div`
  width:40%;
`;


export const Header = styled.div`
  width:100%;
  display:flex;
  flex-direction:column;
  justify-content:flex-start;
  align-items:flex-start;
  margin-top:10px;
  background-color:red;
`;

export const HeaderImage = styled.div`
  width:90%;
  display:flex;
  flex-direction:row;
  justify-content:center;
  align-items:center;
  margin-bottom:10px;
`;

export const HeaderContent = styled.div`
  width:90%;
  display:flex;
  flex-direction:column;
  justify-content:flex-start;

`;

/**
 * color:#ffffff;
font-family:"Darker Grotesque";
font-size:16px;
 */
export const Title = styled.h1`
  font-family:"Cuprum";
  font-size:30px;
  color:#788EBA;
`

export const CategoryContainer = styled.div`
  width:100%;
  display:flex;
  flex-direction:row;
  margin-bottom:10px;
`

export const Alink = styled.a`
  color:${DefaultColor};
  cursor:pointer;
  &:hover{
    color:#ffffff;
  }
`;

export const AButton = styled.a`
  font-family:"Cuprum";
  font-size:16px;
  color:#ffffff;
  cursor:pointer;
  background-color:${DefaultColor};
  padding:5px 10px 5px 10px;
  border-radius:50px;
  margin-left:${props=>props.marginLeft?props.marginLeft:0};

  display:flex;
  flex-direction:row;
  justify-content:flex-start;
  align-items:center;

  text-decoration:none;

  &:hover{
    color:${DefaultColor};
    background-color:#ffffff;
  }
  &:hover .clsIcon{
    color:${DefaultColor};
  }
`;

export const HorizonalScrollContainer = styled.div`
    width:100%;
    display:flex;
    marginTop:50;
    marginBottom:20;
    overflow-x: auto;
`;


export const ListItemRow = styled.div`
  width:100%;
  display:flex;
  flex-direction:row;
  justify-content:space-between;
  align-items:flex-start;
  margin-top:50px;
  margin-bottom:20px;
`;
